import styled from "styled-components"
import { colors, fontSize, fontWeight, spacing } from "../variables"
import ContainerImage from "../assets/container.png"
import ClientsIcon from "../assets/clients-icon.png"
import RecycleIcon from "../assets/recycle-icon.png"

const AboutUsWrapper = styled.section`
  display: flex;
  justify-content: space-around;
  align-items: center;
  margin-bottom: ${spacing.sectionSpacing};
`

const AboutUsText = styled.h2`
  font-size: ${fontSize.text};
  line-height: 2;
  color: ${colors.SECONDARY};
  font-weight: ${fontWeight.regular};
  margin-right: 4rem;
`

const AboutUsImg = styled.img`
  max-width: 50%;
  height: auto;
  flex: 2;
`

const Line = styled.hr`
  width: 35%;
  height: 3px;
  background-color: ${colors.PRIMARY};
  border: none;
  margin-left: inherit;
`

const AboutUsBox = styled.div`
  padding: 0.5rem 1rem;
  border: 1px ${colors.TERCIARY} solid;
  display: flex;
  flex-direction: column;
  align-content: space-around;
  box-shadow: 0 4px 2px -2px ${colors.TERCIARY};
  align-items: center;
  /* flex: 0.9; */
  border-radius: 6px;
  flex: 1;
`
const AboutUsBoxIcon = styled.img`
  height: 60px;
  width: 50px;
`

export const AboutUs = () => {
  return (
    <AboutUsWrapper>
      <div style={{ flex: 1 }}>
        <Line />
        <AboutUsText>
          A Metallum é uma empresa especializada no gerenciamento de resíduos
          não contaminantes situada em uma área de 3.000m² em Praia Grande/SP.{" "}
          <br /> <br /> No mercado desde 2019, e com um time de profissionais
          com mais de 40 anos de atuação, atendemos empresas em toda a região da
          Baixada Santista, especialmente indústrias no polo petroquímico de
          Cubatão e terminais portuários em Santos e Guarujá.
        </AboutUsText>
        {/* <div style={{ display: "flex" }}>
          <AboutUsBox>
            <AboutUsBoxIcon src={ClientsIcon}></AboutUsBoxIcon>
            <p>+30 clientes industriais</p>
          </AboutUsBox>
          <AboutUsBox>
            <AboutUsBoxIcon src={RecycleIcon}></AboutUsBoxIcon>
            <p>+1200 toneladas de sucada recicladas por ano</p>
          </AboutUsBox>
        </div> */}
      </div>
      <div style={{ display: "flex", flexDirection: "column" }}>
        <AboutUsBox>
          <AboutUsBoxIcon src={ClientsIcon}></AboutUsBoxIcon>
          <p>+30 clientes industriais</p>
        </AboutUsBox>
        <AboutUsBox>
          <AboutUsBoxIcon src={RecycleIcon}></AboutUsBoxIcon>
          <p>+1200 toneladas de sucada recicladas por ano</p>
        </AboutUsBox>
      </div>
    </AboutUsWrapper>
  )
}
