import { Nav } from "./components/Nav"
import { Header } from "./components/Header"
import { AboutUs } from "./components/AboutUs"
import styled from "styled-components"
import { Structure } from "./components/Structure"
import { Contact } from "./components/Contact"
import { Footer } from "./components/Footer"
import { NavOption, navOptions, SectionName, structureItems } from "./data"
import { Certifications } from "./components/Certifications"

function App() {
  const BodyWrapper = styled.div`
    padding: 0 25rem;
  `

  const onNavClick = (option: NavOption) => {
    console.log(option)
    const anchor = document.querySelector(`#${option.name}`)
    console.log(anchor)
    // anchor?.scrollIntoView({ behavior: "smooth", block: 'nearest'})
  }

  const getSectionId = (sectionName: SectionName) => {
    return navOptions.find((section => section.name === sectionName))?.id.toString()
  }

  return (
    <div className="App">
      <Nav navOptions={navOptions} handleNavClick={onNavClick}/>
      <Header id={getSectionId(SectionName.Home)}></Header>
      <BodyWrapper>
        <AboutUs id={getSectionId(SectionName.AboutUs)}></AboutUs>
      </BodyWrapper>
        <Structure id={getSectionId(SectionName.Structure)} structureItems={structureItems}></Structure>
      <BodyWrapper>
          <Certifications id={getSectionId(SectionName.Certifications)}></Certifications>
      </BodyWrapper>
        <Contact id={getSectionId(SectionName.Contact)}></Contact>
      <Footer></Footer>
    </div>
  )
}

export default App
