import styled from "styled-components"
import { colors, fontSize, fontWeight, spacing } from "../variables"
import ContainerImage from "../assets/container.jpeg"

const AboutUsWrapper = styled.section`
  display: flex;
  justify-content: center;
  padding: 2rem;
  /* width: 3rem;
  height: 4rem; */
`

const AboutUsText = styled.h2`
  font-size: ${fontSize.title};
  color: ${colors.SECONDARY};
  font-weight: ${fontWeight.regular};
  align-self: center;
  width: 60%;
  flex: ${(props) => props.style?.flex};
`

const AboutUsBox = styled.div`
  width: 3rem;
  height: 3rem;
  background-color: ${(props) => props.style?.backgroundColor};
  opacity: ${(props) => props.style?.opacity};
`

export const AboutUs = () => {
  return (
    <AboutUsWrapper>
      <div style={{ flex: 1 }}>
        <AboutUsBox
          style={{ backgroundColor: colors.SECONDARY, opacity: 0.2 }}
        ></AboutUsBox>
        <AboutUsBox
          style={{ backgroundColor: colors.PRIMARY, opacity: 0.7 }}
        ></AboutUsBox>
      </div>
      <AboutUsText style={{ flex: 2 }}>
        Quem somos <br /> A Metallum é uma empresa especializada no
        gerenciamento de resíduos não contaminantes, situada em uma área de
        3.000m² em Praia Grande/SP. No mercado desde 2019, e com um time de
        profissionais com mais de 40 anos de atuação, atendemos empresas em toda
        a região da Baixada Santista, especialmente indústrias no polo
        petroquímico de Cubatão e terminais portuários em Santos e Guarujá.
      </AboutUsText>
    </AboutUsWrapper>
  )
}
