import styled from "styled-components"
import { colors, fontSize, fontWeight } from "../variables"

const StructureWrapper = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
`

const StructureBox = styled.div`
  display: flex;
  /* flex: 1; */
`

const BoxPadding = styled.div`
  background-color: ${colors.PRIMARY};
  width: 15%;
`

const BoxContent = styled.div`
  padding: 1rem 3rem;
  border: 1px ${colors.TERCIARY} solid;
  display: flex;
  align-content: space-around;
  box-shadow: 0 4px 2px -2px ${colors.TERCIARY};
  align-items: center;
`

const BoxImage = styled.img`
  height: 100px;
  width: 90px;
  margin: 0 3rem;
`

const BoxValue = styled.p`
  color: ${colors.SECONDARY};
  font-weight: ${fontWeight.bold};
  font-size: ${fontSize.superTitle};
`

const BoxName = styled.span`
  text-transform: uppercase;
  font-size: ${fontSize.text};
  color: ${colors.SECONDARY};
  font-weight: ${fontWeight.bold};
`

export interface IStructureProps {
  structureBoxes: StructureInfo[]
}

export interface StructureInfo {
  name: string
  img: string
  value: number
}

export const Structure: React.FC<IStructureProps> = ({ structureBoxes }) => {
  return (
    <StructureWrapper>
      {structureBoxes.map((structure: StructureInfo) => (
        <StructureBox>
          <BoxPadding></BoxPadding>
          <BoxContent>
            <BoxImage src={structure.img}></BoxImage>
            <div>
              <BoxValue>{`+ ${structure.value}`}</BoxValue>
              <BoxName>{structure.name}</BoxName>
            </div>
          </BoxContent>
        </StructureBox>
      ))}
    </StructureWrapper>
  )
}
