import styled from "styled-components"
import { colors, fontSize, fontWeight, spacing } from "../variables"
import Image from "../assets/forms.jpeg"
import { useState } from "react"

const FormSectionBackground = styled.section`
  background-image: linear-gradient(
      rgba(255, 255, 255, 0.413),
      rgba(255, 255, 255, 0.413)
    ),
    url(${Image});

  background-repeat: no-repeat;
  background-size: 100%;
  height: 70vh;
  width: initial;
  display: flex;
  align-items: center;
  justify-content: center;
`

const FormSectionContent = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`

const FormHeader = styled.h2`
  color: ${colors.SECONDARY};
  font-size: ${fontSize.title};
  text-shadow: 0 0 2px ${colors.TERCIARY};
`

const FormWrapper = styled.form`
  background-color: ${colors.TERCIARY};
  padding: 3rem;
  border-radius: 6px;
  height: fit-content;
  width: 35rem;
`

const FormFlexDiv = styled.div`
  display: flex;
  flex-direction: ${(props) => props.style?.flexDirection};
  margin-left: ${(props) => props.style?.marginLeft};
`

const FormLabel = styled.label`
  font-size: ${fontSize.text};
  color: ${colors.PRIMARY};
  font-weight: ${fontWeight.bold};
  margin-bottom: 0.3rem;
`

const FormInput = styled.input`
  border-radius: 6px;
  border: none;
  height: 2rem;
  margin-bottom: 1rem;
  font-family: "Montserrat", sans-serif;
  padding-left: 0.5rem;

  &[type="submit"] {
    cursor: pointer;
    font-weight: ${fontWeight.bold};
    background-color: ${colors.PRIMARY};
    color: ${colors.OFFWHITE};
    margin-top: 1rem;
    height: 48px;
    width: 100%;

    :disabled {
      background-color: ${colors.SECONDARY};
      cursor: not-allowed;
    }
  }
`

const FormTextArea = styled.textarea`
  resize: none;
  border-radius: 6px;
  border: none;
  height: 8rem;
  font-family: "Montserrat", sans-serif;
  padding-left: 0.5rem;
`

export const Contact = () => {
  const [name, setName] = useState("")
  const [telephone, setTelephone] = useState("")
  const [email, setEmail] = useState("")
  const [message, setMessage] = useState("")

  const handleSubmit = (event: Event) => {
    event.preventDefault()

    //mailto
    //mensagem de feedback
  }

  return (
    <FormSectionBackground>
      <FormSectionContent>
        <FormHeader>Fale conosco</FormHeader>
        <FormWrapper onSubmit={() => handleSubmit}>
          <FormFlexDiv>
            <FormFlexDiv style={{ flexDirection: "column" }}>
              <FormLabel>Nome</FormLabel>
              <FormInput
                required
                type="text"
                placeholder="Nome"
                value={name}
                onChange={(event) => setName(event.target.value)}
              ></FormInput>
            </FormFlexDiv>

            <FormFlexDiv
              style={{ flexDirection: "column", marginLeft: "1rem" }}
            >
              <FormLabel>Telefone</FormLabel>
              <FormInput
                required
                type="tel"
                placeholder="Telefone"
                value={telephone}
                onChange={(event) => setTelephone(event.target.value)}
              ></FormInput>
            </FormFlexDiv>
          </FormFlexDiv>
          <FormFlexDiv style={{ flexDirection: "column" }}>
            <FormLabel>E-mail</FormLabel>
            <FormInput
              required
              type="email"
              placeholder="E-mail"
              value={email}
              onChange={(event) => setEmail(event.target.value)}
            ></FormInput>
          </FormFlexDiv>
          <FormFlexDiv style={{ flexDirection: "column" }}>
            <FormLabel>Mensagem</FormLabel>
            <FormTextArea
              placeholder="Escreva sua mensagem"
              required
              value={message}
              onChange={(event) => setMessage(event.target.value)}
            ></FormTextArea>
          </FormFlexDiv>
          <FormInput type="submit" value="Enviar"></FormInput>
        </FormWrapper>
      </FormSectionContent>
    </FormSectionBackground>
  )
}
