import styled from "styled-components"
import { colors, fontSize, fontWeight, spacing } from "../variables"

export const Certifications = () => {
  const CertificationsWrapper = styled.section`
    margin-bottom: ${spacing.sectionSpacing};
  `
  const CertificationsContent = styled.div`
    display: flex;
    justify-content: space-between;
  `

  const CertificationsTitle = styled.h4`
    font-size: ${fontSize.title};
    color: ${colors.TERCIARY};
    flex: 1;
  `

  const CertificationsBox = styled.div`
    padding: 2rem;
    border: 1px solid ${colors.OFFWHITE};
    border-radius: 6px;
    flex: 0.5;
  `

  const CertificationsText = styled.h3`
    color: ${colors.SECONDARY};
    font-size: ${fontSize.smallText};
    font-weight: ${fontWeight.regular};
  `

  return (
    <CertificationsWrapper>
      <CertificationsContent>
        <CertificationsTitle>
          Empresa certificada e licenciada
        </CertificationsTitle>
        <CertificationsBox>
          <CertificationsText>
            A Metallum está situada em uma área de 3.000m² em Praia Grande, em
            São Paulo. Somos licenciados pela Cetesb (Licença de Operação e
            SIGOR) e também possuimos licença federal junto ao IBAMA (Cadastro
            Técnico Federal Protocolo AFE).
          </CertificationsText>
        </CertificationsBox>
      </CertificationsContent>
    </CertificationsWrapper>
  )
}
