import styled from "styled-components"
import HeaderImage from "../assets/header.jpg"
import { colors, fontSize, fontWeight, spacing } from "../variables"

const HeaderSection = styled.header`
  background-image: url("${HeaderImage}");
  background-size: 100%;
  background-position-y: 50%;
  background-repeat: no-repeat;
  height: 70vh;
  position: relative;
  opacity: 0.9;
`

const HeaderContentWrapper = styled.div`
  position: absolute;
  bottom: 28%;
`

const HeaderContent = styled.h1`
  font-weight: lighter;
  font-size: ${fontSize.superTitle};
  line-height: 1.5;
  margin: 8px 0;
  /* display: inline-block; */
  padding-left: 6rem;
  padding-right: 1rem;
`

const HeaderText = styled.h1`
  color: ${(props) => props.color};
  font-weight: ${(props) => props.style?.fontWeight};
  margin: 8px 0;
  padding-left: 6rem;
  padding-right: 1rem;
  font-size: ${fontSize.superTitle};
`

const HeaderContentBackground = styled.div`
  background-color: rgba(254, 254, 254, 0.8);
  width: fit-content;
  height: fit-content;
`

export const Header: React.FC = () => {
  return (
    <HeaderSection>
      <HeaderContentWrapper>
        <HeaderContentBackground>
          <HeaderText style={{ fontWeight: "bold", color: colors.PRIMARY }}>
            ReferÃªncia em reciclagem
          </HeaderText>
        </HeaderContentBackground>
      </HeaderContentWrapper>
    </HeaderSection>
  )
}
