import logo from "../../assets/logo-metallum.png"
import styled from "styled-components"
import { colors } from "../../variables"

const HeaderSection = styled.header`
  height: 100px;
  padding: 30px 0;
  background-color: #fff;
  border-bottom: 1px solid ${colors.TERCIARY};
  display: flex;
  flex-direction: row;
  justify-content: space-around;
  width: 100%;
`

const Logo = styled.img`
  height: 70px;
  align-self: center;
`

const HeaderOptionsWrapper = styled.ul`
  list-style-type: none;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0;
  flex: 0.35;
`

const HeaderOption = styled.li`
  color: ${colors.TERCIARY};
  cursor: pointer;
  position: relative;

  :hover {
  }
`

const Anchor = styled.a`
  text-decoration: none;
  color: inherit;

  :hover {
    color: ${colors.PRIMARY};
    text-decoration: none;
    cursor: pointer;
  }
`

export interface IHeaderProps {
  headerOptions: string[]
}

export const Header: React.FC<IHeaderProps> = ({ headerOptions }) => {
  return (
    <HeaderSection>
      <Logo src={logo} alt="Logo Metallum" />
      <HeaderOptionsWrapper>
        {headerOptions.map((option: string) => (
          <HeaderOption>
            <Anchor href="www.google.com">{option}</Anchor>
          </HeaderOption>
        ))}
      </HeaderOptionsWrapper>
    </HeaderSection>
  )
}
