import logo from "../assets/logo-metallum.png"
import styled from "styled-components"
import { colors, spacing } from "../variables"

const NavSection = styled.nav`
  height: fit-content;
  padding: ${spacing.horizontalPadding};
  background-color: #fff;
  display: flex;
  flex-direction: row;
  justify-content: space-around;
`

const Logo = styled.img`
  height: 4em;
  align-self: center;
  cursor: pointer;
`

const NavOptionsWrapper = styled.ul`
  list-style-type: none;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0;
  flex: 0.45;
`

const NavOption = styled.li`
  color: ${colors.TERCIARY};
  cursor: pointer;
  position: relative;
`

const Anchor = styled.a`
  text-decoration: none;
  color: inherit;
  /* letter-spacing: 0.01em; */
  display: inline-block;
  padding: ${spacing.horizontalPadding} ${spacing.verticalPadding};
  position: relative;
  cursor: pointer;

  :hover {
    color: ${colors.PRIMARY};
  }

  :hover:after {
    width: 100%;
    left: 0;
  }

  :after {
    background: none repeat scroll 0 0 transparent;
    bottom: 0;
    top: -28;
    content: "";
    display: block;
    height: 2px;
    left: 40%;
    position: absolute;
    background: ${colors.PRIMARY};
    transition: width 0.3s ease 0s, left 0.3s ease 0s;
    width: 0;
  }
`

export interface INavProps {
  navOptions: string[]
}

export const Nav: React.FC<INavProps> = ({ navOptions }) => {
  return (
    <NavSection>
      <Logo src={logo} alt="Logo Metallum" />
      <NavOptionsWrapper>
        {navOptions.map((option: string) => (
          <NavOption>
            <Anchor href="www.google.com">{option}</Anchor>
          </NavOption>
        ))}
      </NavOptionsWrapper>
    </NavSection>
  )
}
