import styled from "styled-components"
import { colors, fontSize, fontWeight, spacing } from "../variables"
import { useState } from "react"
import { send } from "emailjs-com"

// const FormSectionBackground = styled.section`
//   display: flex;
//   align-items: center;
//   justify-content: center;
//   margin-bottom: ${spacing.sectionSpacing};
// `

const FormSectionContent = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`

const Line = styled.hr`
  width: 30%;
  height: 2px;
  background-color: ${colors.PRIMARY};
  border: none;
  margin: 1rem auto;
`

const FormHeader = styled.h2`
  color: ${colors.SECONDARY};
  font-size: ${fontSize.title};
  font-weight: ${fontWeight.regular};
  text-shadow: 0 0 2px ${colors.TERCIARY};
`

const FormWrapper = styled.form`
  background-color: ${colors.OFFWHITE};
  padding: 3rem;
  border-radius: 6px;
  height: fit-content;
  width: 35rem;
`

const FormFlexDiv = styled.div`
  display: flex;
  flex-direction: ${(props) => props.style?.flexDirection};
  margin-left: ${(props) => props.style?.marginLeft};
`

const FormLabel = styled.label`
  font-size: ${fontSize.text};
  color: ${colors.PRIMARY};
  font-weight: ${fontWeight.bold};
  margin-bottom: 0.3rem;
`

const FormInput = styled.input`
  border-radius: 6px;
  border: none;
  height: 2rem;
  margin-bottom: 1rem;
  font-family: "Montserrat", sans-serif;
  padding-left: 0.5rem;
  background-color: ${colors.TERCIARY};

  -internal-autofill-selected {
    background-color: ${colors.TERCIARY};
  }

  :focus-visible {
    outline: none;
  }

  &[type="submit"] {
    cursor: pointer;
    font-weight: ${fontWeight.bold};
    background-color: ${colors.PRIMARY};
    color: ${colors.OFFWHITE};
    margin-top: 1rem;
    height: 48px;
    width: 100%;

    :disabled {
      background-color: ${colors.SECONDARY};
      cursor: not-allowed;
    }
  }
`

const FormTextArea = styled.textarea`
  resize: none;
  border-radius: 6px;
  border: none;
  height: 8rem;
  font-family: "Montserrat", sans-serif;
  padding-left: 0.5rem;

  :focus-visible {
    outline: none;
  }
`

const FormEmailFeedback = styled.p`
  font-size: ${fontSize.smallText};
  color: ${colors.SECONDARY};
  font-weight: ${fontWeight.bold};
  text-align: center;
`

interface EmailParams {
  subject: string
  text: string
  access_token: string
}

export const Contact = () => {
  const [name, setName] = useState("")
  const [telephone, setTelephone] = useState("")
  const [email, setEmail] = useState("")
  const [message, setMessage] = useState("")
  const [emailFeedback, setEmailFeedback] = useState("")

  const handleSubmit = (event: any) => {
    event.preventDefault()
    sendEmail()
  }

  const sendEmail = () => {
    const postMailUrl = "https://postmail.invotes.com/send"
    const accessToken = "ik7adfw8vpm6lqhkzz4haltq"
    const emailParams: EmailParams = {
      subject: "Novo contato via site",
      text: message,
      access_token: accessToken,
    }

    fetch(postMailUrl, {
      method: "POST",
      headers: { "Content-Type": "application/x-www-form-urlencoded" },
      body: toParams(emailParams),
    })
      .then((response) => {
        console.log(toParams(emailParams))
        if (response.status === 200) {
          // setTimeout(() => onSendEmailSuccess(), 2000)
          // setEmailFeedback("")
        } else {
          console.log(toParams(emailParams))

          // setTimeout(() => onEmailError(), 2000)
          // setEmailFeedback("")
        }
      })
      .catch(() => {
        console.log(toParams(emailParams))

        // setTimeout(() => onEmailError(), 2000)
        // setEmailFeedback("")
      })
  }

  // const generateText = () => {
  //   return `Nome: ${name} \n Telefone: ${telephone} \n E-mail: ${email} \n Mensagem: ${message}`
  // }

  const onSendEmailSuccess = () => {
    setEmailFeedback(
      "Seu e-mail foi enviado. Iremos retornar o contato assim que o possível.",
    )
  }

  const onEmailError = () => {
    setEmailFeedback(
      "Não conseguimos enviar seu e-mail. Se preferir, escreva diretamente para metallumsucatas@outlook.com, ou tente novamente mais tarde",
    )
  }

  const toParams = (data_js: any) => {
    let form_data = []
    for (var key in data_js) {
      form_data.push(
        `${encodeURIComponent(key)}= ${encodeURIComponent(data_js[key])}`,
      )
    }
    return form_data.join("&")
  }

  return (
    <FormSectionBackground>
      <FormSectionContent>
        <Line />
        <FormHeader>Precisa falar conosco?</FormHeader>
        <FormWrapper onSubmit={handleSubmit}>
          <input
            type="hidden"
            name="access_token"
            value="ik7adfw8vpm6lqhkzz4haltq"
          />
          <FormFlexDiv>
            <FormFlexDiv style={{ flexDirection: "column" }}>
              <FormLabel>Nome</FormLabel>
              <FormInput
                required
                type="text"
                placeholder="Nome"
                value={name}
                onChange={(event) => setName(event.target.value)}
              ></FormInput>
            </FormFlexDiv>

            <FormFlexDiv
              style={{ flexDirection: "column", marginLeft: "1rem" }}
            >
              <FormLabel>Telefone</FormLabel>
              <FormInput
                required
                type="tel"
                placeholder="Telefone"
                value={telephone}
                onChange={(event) => setTelephone(event.target.value)}
              ></FormInput>
            </FormFlexDiv>
          </FormFlexDiv>
          <FormFlexDiv style={{ flexDirection: "column" }}>
            <FormLabel>E-mail</FormLabel>
            <FormInput
              required
              type="email"
              placeholder="E-mail"
              value={email}
              onChange={(event) => setEmail(event.target.value)}
            ></FormInput>
          </FormFlexDiv>
          <FormFlexDiv style={{ flexDirection: "column" }}>
            <FormLabel>Mensagem</FormLabel>
            <FormTextArea
              placeholder="Escreva sua mensagem"
              required
              value={message}
              onChange={(event) => setMessage(event.target.value)}
            ></FormTextArea>
          </FormFlexDiv>
          <FormInput type="submit" value="Enviar"></FormInput>
          <FormEmailFeedback>{emailFeedback}</FormEmailFeedback>
        </FormWrapper>
      </FormSectionContent>
    </FormSectionBackground>
  )
}
