import styled from "styled-components"
import { colors, fontSize, fontWeight, spacing } from "../variables"
import Image from "../assets/forms.jpeg"

const FormSection = styled.section`
  margin-bottom: ${spacing.sectionSpacing};
  background-image: url(${Image});
  opacity: 0.7;
  background-repeat: no-repeat;
  background-size: 100%;
  height: 65vh;
  display: flex;
  justify-content: center;
`

const FormWrapper = styled.form`
  /* display: flex;
  flex-direction: column; */
  background-color: ${colors.TERCIARY};
  padding: 3rem;
  border-radius: 6px;
  /* align-self: center;
  justify-content: right; */
  width: min-content;
`

const FormFlexDiv = styled.div`
  display: flex;
  flex-direction: ${(props) => props.style?.flexDirection};
  margin-bottom: ${(props) => props.style?.marginBottom}; ;
`

const FormLabel = styled.label`
  font-size: ${fontSize.text};
  color: ${colors.PRIMARY};
  font-weight: ${fontWeight.bold};
`

const FormInput = styled.input`
  border-radius: 6px;
  border: 1px solid ${colors.SECONDARY};
  height: 2rem;
`

const FormButton = styled.button``

const FormTextArea = styled.textarea``

export const Contact = () => {
  return (
    <FormSection>
      <FormWrapper>
        <div
          style={{
            display: "flex",
            alignItems: "center",
            marginBottom: "2rem",
          }}
        >
          <FormFlexDiv style={{ flexDirection: "column" }}>
            <FormLabel>Nome</FormLabel>
            <FormInput type="text" value="name" placeholder="Nome"></FormInput>
          </FormFlexDiv>

          <FormFlexDiv style={{ flexDirection: "column" }}>
            <FormLabel>E-mail</FormLabel>
            <FormInput
              type="email"
              value="email"
              placeholder="E-mail"
            ></FormInput>
          </FormFlexDiv>

          <div style={{ display: "flex", flexDirection: "column" }}>
            <FormLabel>Telefone</FormLabel>
            <FormInput
              type="tel"
              value="telefone"
              placeholder="Telefone"
            ></FormInput>
          </div>
        </div>
        <FormLabel>
          Mensagem
          <FormTextArea></FormTextArea>
        </FormLabel>
      </FormWrapper>
    </FormSection>
  )
}
