import styled from "styled-components"
import { colors, fontSize, fontWeight, spacing } from "../variables"
import Image from "../assets/forms.jpeg"

const FormSection = styled.section`
  margin-bottom: ${spacing.sectionSpacing};
  background-image: url(${Image});
  opacity: 0.7;
  background-repeat: no-repeat;
  background-size: 100%;
  height: 65vh;
  display: flex;
  justify-content: center;
`

const FormWrapper = styled.form`
  background-color: ${colors.TERCIARY};
  padding: 3rem;
  border-radius: 6px;
`

const FormFlexDiv = styled.div`
  display: flex;
  flex-direction: ${(props) => props.style?.flexDirection};
  margin-left: ${(props) => props.style?.marginLeft};
`

const FormLabel = styled.label`
  font-size: ${fontSize.text};
  color: ${colors.PRIMARY};
  font-weight: ${fontWeight.bold};
  margin-bottom: 0.3rem;
`

const FormInput = styled.input`
  border-radius: 6px;
  border: 1px solid ${colors.SECONDARY};
  height: 2rem;
  margin-bottom: 1rem;
  width: 100%;

  &[type="submit"] {
    cursor: pointer;
    font-weight: ${fontWeight.bold};
    background-color: ${colors.PRIMARY};
    color: ${colors.OFFWHITE};
    border: 1px solid ${colors.OFFWHITE};
    margin-top: 1rem;
    height: 48px;
    /* margin-bottom: 3rem; */

    :disabled {
      background-color: ${colors.SECONDARY};
    }
  }
`

const FormButton = styled.button``

const FormTextArea = styled.textarea`
  resize: none;
  border-radius: 6px;
  border: 1px solid ${colors.SECONDARY};
  height: 8rem;
`

export const Contact = () => {
  return (
    <FormSection>
      <FormWrapper>
        <FormFlexDiv>
          <FormFlexDiv style={{ flexDirection: "column" }}>
            <FormLabel>Nome</FormLabel>
            <FormInput
              required
              type="text"
              value="name"
              placeholder="Nome"
            ></FormInput>
          </FormFlexDiv>

          <FormFlexDiv style={{ flexDirection: "column", marginLeft: "1rem" }}>
            <FormLabel>Telefone</FormLabel>
            <FormInput
              required
              type="tel"
              value="telefone"
              placeholder="Telefone"
            ></FormInput>
          </FormFlexDiv>
        </FormFlexDiv>
        <FormFlexDiv style={{ flexDirection: "column" }}>
          <FormLabel>E-mail</FormLabel>
          <FormInput
            required
            type="email"
            value="email"
            placeholder="E-mail"
          ></FormInput>
        </FormFlexDiv>
        <FormFlexDiv style={{ flexDirection: "column" }}>
          <FormLabel>Mensagem</FormLabel>
          <FormTextArea
            placeholder="Escreva sua mensagem"
            required
          ></FormTextArea>
        </FormFlexDiv>
        <div>
          <FormInput type="submit" disabled></FormInput>
        </div>
      </FormWrapper>
    </FormSection>
  )
}
