import logo from "../../assets/logo-metallum.png"
import styled from "styled-components"
import { colors } from "../../variables"

const HeaderSection = styled.header`
  height: fit-content;
  padding: 30px 0;
  background-color: #fff;
  border-bottom: 1px solid ${colors.TERCIARY};
  display: flex;
  flex-direction: row;
  justify-content: space-around;
  width: 100%;
`

const Logo = styled.img`
  height: 70px;
`

const HeaderOptionsWrapper = styled.ul`
  list-style-type: none;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0;
  flex: 0.35;
`

const HeaderOption = styled.li`
  color: ${colors.TERCIARY};
  cursor: pointer;

  :hover {
    color: ${colors.PRIMARY};
    /* text-decoration: underline 3px; */
    border-bottom: 3px solid ${colors.PRIMARY};
  }
`

export interface IHeaderProps {
  headerOptions: string[]
}

export const Header: React.FC<IHeaderProps> = ({ headerOptions }) => {
  return (
    <HeaderSection>
      <Logo src={logo} alt="Logo Metallum" />
      <HeaderOptionsWrapper>
        {headerOptions.map((option: string) => (
          <HeaderOption>{option}</HeaderOption>
        ))}
      </HeaderOptionsWrapper>
    </HeaderSection>
  )
}
