import styled from "styled-components"
import { colors, fontSize, fontWeight, spacing } from "../variables"
import ContainerImage from "../assets/teste.jpeg"

const AboutUsWrapper = styled.section`
  display: flex;
  justify-content: space-around;
  padding: 7rem;
  /* width: 3rem;
  height: 4rem; */
`

const AboutUsText = styled.h2`
  font-size: ${(props) => props.style?.fontSize};
  line-height: 2;
  color: ${colors.SECONDARY};
  font-weight: ${(props) => props.style?.fontWeight};
  padding: 0 15rem;
`

const AboutUsBox = styled.h2`
  width: ${(props) => props.style?.width};
  height: ${(props) => props.style?.height};
  background-color: ${(props) => props.style?.backgroundColor};
  opacity: ${(props) => props.style?.opacity};
  position: ${(props) => props.style?.position};
  bottom: ${(props) => props.style?.bottom};
  left: ${(props) => props.style?.left};
  margin: 0;
`

export const AboutUs = () => {
  return (
    <AboutUsWrapper>
        <AboutUsBox
          style={{
            backgroundColor: colors.SECONDARY,
            opacity: 0.1,
            position: "absolute",
            bottom: -110,
            left: 290,
            width: '16rem',
            height: '12rem',
          }}
        ></AboutUsBox>
        <AboutUsBox
          style={{
            backgroundColor: colors.PRIMARY,
            opacity: 0.5,
            position: "absolute",
            bottom: 70,
            left: 146,
            width: '4rem',
            height: '4rem',
          }}
        ></AboutUsBox>
        <img
          src={ContainerImage}
          width="400px"
          height="200px"
          style={{ flex: 1, position: 'relative'}}
        />

      <AboutUsText style={{ flex: 2, fontSize: fontSize.text, fontWeight: "normal" }}>
        <span style={{ fontWeight: "bold", fontSize: fontSize.title }}> A Metallum </span>
        é uma empresa especializada no gerenciamento de resíduos não
        contaminantes, situada em uma área de 3.000m² em Praia Grande/SP. No
        mercado desde 2019, e com um time de profissionais com mais de 40 anos
        de atuação, atendemos empresas em toda a região da Baixada Santista,
        especialmente indústrias no polo petroquímico de Cubatão e terminais
        portuários em Santos e Guarujá.
      </AboutUsText>
    </AboutUsWrapper>
  )
}
