import styled from "styled-components"
import { colors, fontSize, fontWeight, spacing } from "../variables"
import teste from "../assets/teste.jpeg"
import { StructureInfo } from "../data"

const StructureWrapper = styled.section`
  margin-bottom: ${spacing.sectionSpacing};
  background-image: url(${teste});
  background-repeat: no-repeat;
  background-position-x: 9%;
  border-radius: 6px;
`
const StructureContent = styled.div`
  padding: 1rem 0;
`

const StructureTitle = styled.h2`
  text-transform: uppercase;
  color: ${colors.PRIMARY};
  font-weight: ${fontWeight.bold};
`

const StructureSubtitle = styled.p`
  color: ${colors.SECONDARY};
`

const StructureList = styled.ul`
  list-style: none;
  padding: 0;
  line-height: 2;
`

const StructureItem = styled.li`
  color: ${colors.TERCIARY};
`

const StructureRow = styled.div`
  display: flex;
  /* align-items: center; */
  justify-content: space-between;
  flex-direction: column;
  margin-bottom: 2rem;
  /* background-color: ${colors.TERCIARY}; */
`

const StructureBox = styled.div`
  display: flex;
  flex: 0.5;
  border-radius: 6px;
  margin-bottom: 1rem;

  :hover {
    background-color: ${colors.TERCIARY};
    color: ${colors.OFFWHITE};
  }
`

const BoxPadding = styled.div`
  background-color: ${colors.PRIMARY};
  flex: 0.1;
`

const BoxContent = styled.div`
  padding: 0.5rem 3rem;
  border: 1px ${colors.TERCIARY} solid;
  display: flex;
  align-content: space-around;
  box-shadow: 0 4px 2px -2px ${colors.TERCIARY};
  align-items: center;
  flex: 0.9;
`
const BoxValue = styled.p`
  color: ${colors.SECONDARY};
  font-weight: ${fontWeight.bold};
  font-size: ${fontSize.superTitle};
  margin-bottom: 0.5rem;
  margin-top: 0;
  padding-right: 0.5rem;
`

const BoxName = styled.span`
  text-transform: uppercase;
  font-size: ${fontSize.smallText};
  color: ${colors.SECONDARY};
  font-weight: ${fontWeight.bold};
`

const StructureImage = styled.img`
  width: 200px;
  height: 200px;
`

export interface IStructureProps {
  structureItems: StructureInfo[]
}

export const Structure: React.FC<IStructureProps> = ({ structureItems }) => {
  return (
    <StructureWrapper>
      <StructureContent>
        <StructureTitle>CONHEÇA NOSSA ESTRUTURA</StructureTitle>
        <StructureSubtitle>
          Temos os equipamentos ideais para prestar um serviço de excelência{" "}
        </StructureSubtitle>
        <StructureList>
          {structureItems.map((item) => (
            <li>
              {" "}
              {item.value} {item.name}{" "}
            </li>
          ))}
        </StructureList>

        {/* <StructureRow>
          {structureBoxes1.map((structure: StructureInfo) => (
            <StructureBox>
              <BoxPadding></BoxPadding>
              <BoxContent>
                <BoxValue>{structure.value}</BoxValue>
                <BoxName>{structure.name}</BoxName>
              </BoxContent>
            </StructureBox>
          ))}
        </StructureRow>
        <StructureRow>
          {structureBoxes2.map((structure: StructureInfo) => (
            <StructureBox>
              <BoxPadding></BoxPadding>
              <BoxContent>
                <BoxValue>{structure.value}</BoxValue>
                <BoxName>{structure.name}</BoxName>
              </BoxContent>
            </StructureBox>
          ))}
        </StructureRow> */}
      </StructureContent>
    </StructureWrapper>
  )
}
