import styled from "styled-components"
import { colors, fontSize, fontWeight, spacing } from "../variables"
import Image from "../assets/forms.jpeg"

const FormSectionBackground = styled.section`
  background-image: url(${Image});
  opacity: 0.7;
  background-repeat: no-repeat;
  background-size: 100%;
  height: 65vh;
  display: flex;
  align-self: center;
`

const FormHeader = styled.h2`
  color: ${colors.SECONDARY};
`

const FormWrapper = styled.form`
  background-color: ${colors.TERCIARY};
  padding: 3rem;
  border-radius: 6px;
  height: fit-content;
  /* flex: 1; */
  /* position: absolute;
  right: 20%; */
`

const FormFlexDiv = styled.div`
  display: flex;
  flex-direction: ${(props) => props.style?.flexDirection};
  margin-left: ${(props) => props.style?.marginLeft};
`

const FormLabel = styled.label`
  font-size: ${fontSize.text};
  color: ${colors.PRIMARY};
  font-weight: ${fontWeight.bold};
  margin-bottom: 0.3rem;
`

const FormInput = styled.input`
  border-radius: 6px;
  border: none;
  height: 2rem;
  margin-bottom: 1rem;
  width: 100%;
  font-family: "Montserrat", sans-serif;
  padding-left: 0.5rem;

  &[type="submit"] {
    cursor: pointer;
    font-weight: ${fontWeight.bold};
    background-color: ${colors.PRIMARY};
    color: ${colors.OFFWHITE};
    margin-top: 1rem;
    height: 48px;

    :disabled {
      background-color: ${colors.SECONDARY};
      cursor: not-allowed;
    }
  }
`

const FormTextArea = styled.textarea`
  resize: none;
  border-radius: 6px;
  border: none;
  height: 8rem;
  font-family: "Montserrat", sans-serif;
  padding-left: 0.5rem;
`

export const Contact = () => {
  return (
    <FormSectionBackground>
      <div
        style={{
          // flex: 2,
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          flexDirection: "column",
        }}
      >
        <FormHeader>Que tal fazer seu or√ßamento?</FormHeader>
        <FormWrapper>
          <FormFlexDiv>
            <FormFlexDiv style={{ flexDirection: "column" }}>
              <FormLabel>Nome</FormLabel>
              <FormInput required type="text" placeholder="Nome"></FormInput>
            </FormFlexDiv>

            <FormFlexDiv
              style={{ flexDirection: "column", marginLeft: "1rem" }}
            >
              <FormLabel>Telefone</FormLabel>
              <FormInput required type="tel" placeholder="Telefone"></FormInput>
            </FormFlexDiv>
          </FormFlexDiv>
          <FormFlexDiv style={{ flexDirection: "column" }}>
            <FormLabel>E-mail</FormLabel>
            <FormInput required type="email" placeholder="E-mail"></FormInput>
          </FormFlexDiv>
          <FormFlexDiv style={{ flexDirection: "column" }}>
            <FormLabel>Mensagem</FormLabel>
            <FormTextArea
              placeholder="Escreva sua mensagem"
              required
            ></FormTextArea>
          </FormFlexDiv>
          <FormInput type="submit"></FormInput>
        </FormWrapper>
      </div>
    </FormSectionBackground>
  )
}
