export const Test = () => {
  var form_id_js = "javascript_form";

    var data_js = {
      "access_token": "ik7adfw8vpm6lqhkzz4haltq",
      "subject": "",
      "text": ""
    };
    var sendButton = document.getElementById("js_send")?.onclick;

    function js_send(event: Event) {
        event?.preventDefault()
        var request = new XMLHttpRequest();
        request.onreadystatechange = function() {
        };

        let subject = (document.querySelector(`#${form_id_js}[name='subject']`) as HTMLInputElement).value;
        let text = (document.querySelector(`#${form_id_js}[name='text']`) as HTMLInputElement).value;

        data_js['subject'] = subject;
        data_js['text'] = text;
        var params = toParams(data_js);



        request.open("POST", "https://postmail.invotes.com/send", true);
        request.setRequestHeader("Content-type", "application/x-www-form-urlencoded");

        request.send(params);

        return false;
    }

    sendButton = js_send;

    function toParams(data_js: any) {
        var form_data = [];
        for ( var key in data_js ) {
            form_data.push(encodeURIComponent(key) + "=" + encodeURIComponent(data_js[key]));
        }

        return form_data.join("&");
    }

    var js_form = document.getElementById(form_id_js);
    js_form?.addEventListener("submit", function (e) {
        e.preventDefault();
    });
  return (
    <form id="javascript_form">
    <input type="text" name="subject" placeholder="Subject" />
    <textarea name="text" placeholder="Message"></textarea>
    <input type="submit" id="js_send" value="Send" />

    <p>Powered by <a href="https://postmail.invotes.com" target="_blank">PostMail</a></p>
</form>
  )
}
