import styled from "styled-components"
import { colors, fontSize, fontWeight, spacing } from "../variables"
import { useState } from "react"
import { send } from "emailjs-com"

const FormSectionContent = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  margin-bottom: ${spacing.sectionSpacing};
`

const Line = styled.hr`
  width: 30%;
  height: 2px;
  background-color: ${colors.PRIMARY};
  border: none;
  margin: 1rem auto;
`

const FormHeader = styled.h2`
  color: ${colors.SECONDARY};
  font-size: ${fontSize.title};
  font-weight: ${fontWeight.regular};
  text-shadow: 0 0 2px ${colors.TERCIARY};
`

const FormWrapper = styled.form`
  background-color: ${colors.OFFWHITE};
  padding: 3rem;
  border-radius: 6px;
  height: fit-content;
  width: 35rem;
`

const FormFlexDiv = styled.div`
  display: flex;
  flex-direction: ${(props) => props.style?.flexDirection};
  margin-left: ${(props) => props.style?.marginLeft};
`

const FormLabel = styled.label`
  font-size: ${fontSize.text};
  color: ${colors.PRIMARY};
  font-weight: ${fontWeight.bold};
  margin-bottom: 0.3rem;
`

const FormInput = styled.input`
  border-radius: 6px;
  border: none;
  height: 2rem;
  margin-bottom: 1rem;
  font-family: "Montserrat", sans-serif;
  padding-left: 0.5rem;
  background-color: ${colors.TERCIARY};

  -internal-autofill-selected {
    background-color: ${colors.TERCIARY};
  }

  :focus-visible {
    outline: none;
  }

  &[type="submit"] {
    cursor: pointer;
    font-weight: ${fontWeight.bold};
    background-color: ${colors.PRIMARY};
    color: ${colors.OFFWHITE};
    margin-top: 1rem;
    height: 48px;
    width: 100%;

    :disabled {
      background-color: ${colors.SECONDARY};
      cursor: not-allowed;
    }
  }
`

const FormTextArea = styled.textarea`
  resize: none;
  border-radius: 6px;
  border: none;
  height: 8rem;
  font-family: "Montserrat", sans-serif;
  padding-left: 0.5rem;

  :focus-visible {
    outline: none;
  }
`

const FormEmailFeedback = styled.p`
  font-size: ${fontSize.smallText};
  color: ${colors.SECONDARY};
  font-weight: ${fontWeight.bold};
  text-align: center;
`

interface EmailParams {
  subject: string
  text: string
  access_token: string
}

export const Contact = () => {
  const [name, setName] = useState("")
  const [telephone, setTelephone] = useState("")
  const [email, setEmail] = useState("")
  const [message, setMessage] = useState("")
  const [emailFeedback, setEmailFeedback] = useState("")

  const handleSubmit = (event: any) => {
    event.preventDefault()
    sendEmail()
  }

  const sendEmail = () => {
    const postMailUrl = "https://postmail.invotes.com/send"
    const accessToken = "ik7adfw8vpm6lqhkzz4haltq"
    const emailParams: EmailParams = {
      subject: "Novo contato via site",
      text: message,
      access_token: accessToken,
    }

    fetch(postMailUrl, {
      method: "POST",
      headers: { "Content-Type": "application/x-www-form-urlencoded" },
      body: toParams(emailParams),
    })
      .then((response) => {
        console.log(toParams(emailParams))
        if (response.status === 200) {
          // setTimeout(() => onSendEmailSuccess(), 2000)
          // setEmailFeedback("")
        } else {
          console.log(toParams(emailParams))

          // setTimeout(() => onEmailError(), 2000)
          // setEmailFeedback("")
        }
      })
      .catch(() => {
        console.log(toParams(emailParams))

        // setTimeout(() => onEmailError(), 2000)
        // setEmailFeedback("")
      })
  }

  // const generateText = () => {
  //   return `Nome: ${name} \n Telefone: ${telephone} \n E-mail: ${email} \n Mensagem: ${message}`
  // }

  const onSendEmailSuccess = () => {
    setEmailFeedback(
      "Seu e-mail foi enviado. Iremos retornar o contato assim que o possível.",
    )
  }

  const onEmailError = () => {
    setEmailFeedback(
      "Não conseguimos enviar seu e-mail. Se preferir, escreva diretamente para metallumsucatas@outlook.com, ou tente novamente mais tarde",
    )
  }

  const toParams = (data_js: any) => {
    let form_data = []
    for (var key in data_js) {
      form_data.push(
        `${encodeURIComponent(key)}= ${encodeURIComponent(data_js[key])}`,
      )
    }
    return form_data.join("&")
  }

  return (
      <FormSectionContent>
        <Line />
        <FormHeader>Precisa falar conosco?</FormHeader>
        <div style={{ display: "flex"}}>
    <svg width="50px" height="50px" viewBox="0 0 1219.547 1225.016">
        <path fill="#E0E0E0" d="M1041.858 178.02C927.206 63.289 774.753.07 612.325 0 277.617 0 5.232 272.298 5.098 606.991c-.039 106.986 27.915 211.42 81.048 303.476L0 1225.016l321.898-84.406c88.689 48.368 188.547 73.855 290.166 73.896h.258.003c334.654 0 607.08-272.346 607.222-607.023.056-162.208-63.052-314.724-177.689-429.463zm-429.533 933.963h-.197c-90.578-.048-179.402-24.366-256.878-70.339l-18.438-10.93-191.021 50.083 51-186.176-12.013-19.087c-50.525-80.336-77.198-173.175-77.16-268.504.111-278.186 226.507-504.503 504.898-504.503 134.812.056 261.519 52.604 356.814 147.965 95.289 95.36 147.728 222.128 147.688 356.948-.118 278.195-226.522 504.543-504.693 504.543z"></path>
        <linearGradient id="htwaicona-chat" gradientUnits="userSpaceOnUse" x1="609.77" y1="1190.114" x2="609.77" y2="21.084">
            <stop offset="0" stop-color="#20b038"></stop>
