import styled from "styled-components"
import { colors, fontSize, fontWeight, spacing } from "../variables"
import ContainerImage from "../assets/container.png"

const AboutUsWrapper = styled.section`
  display: flex;
  justify-content: space-around;
  align-items: center;
  padding: 8rem 10rem;
  background-color: rgba(254,254,254,0.8)
`

const AboutUsText = styled.h2`
  font-size: ${(props) => props.style?.fontSize};
  line-height: 2;
  color: ${colors.SECONDARY};
  font-weight: ${(props) => props.style?.fontWeight};
  margin-left: 9rem;
`

const AboutUsImg = styled.img`
  width: 430px;
  height: 300px;
  flex: 1;
  position: relative;
`

export const AboutUs = () => {
  return (
    <AboutUsWrapper>
      <AboutUsImg src={ContainerImage} />
      <AboutUsText>A Metallum é uma empresa especializada no gerenciamento de resíduos não contaminantes, situada em uma área de 3.000m² em Praia Grande/SP. <br /> <br /> No mercado desde 2019, e com um time de profissionais com mais de 40 anos de atuação, atendemos empresas em toda a região da Baixada Santista, especialmente indústrias no polo petroquímico de Cubatão e terminais portuários em Santos e Guarujá.</AboutUsText>
    </AboutUsWrapper>
  )
}
