import styled from "styled-components"
import { flattenDiagnosticMessageText } from "typescript"
import { colors } from "../variables"

const StructureWrapper = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-evenly;
`

const StructureBox = styled.div`
  /* height: fit-content; */
  display: flex;
  /* align-items: center; */
`

const BoxPadding = styled.div`
  background-color: ${colors.PRIMARY};
  width: 20%;
`

export interface IStructureProps {
  structureBoxes: StructureInfo[]
}

export interface StructureInfo {
  name: string
  img: string
  value: number
}

export const Structure: React.FC<IStructureProps> = ({ structureBoxes }) => {
  return (
    <StructureWrapper>
      {structureBoxes.map((structure: StructureInfo) => (
        <StructureBox>
          <BoxPadding></BoxPadding>
          <div style={{ display: flattenDiagnosticMessageText }}>
            <img src={structure.img}></img>
            <div>
              <p>{`+ ${structure.value}`}</p>
              <span>{structure.name}</span>
            </div>
          </div>
        </StructureBox>
      ))}
    </StructureWrapper>
  )
}
